<script setup>
import { useUserStore } from "~/stores/user"

definePageMeta({
    middleware: ['auth']
})
const router = useRouter()
const toast = useToast()
const useUser = useUserStore()
const user = useUser.user
const openProfile = ref(false)
const isdisabled = ref(false)
const newTopicTitle = ref("")

const {data} = await useFetch(useRuntimeConfig().public.apiBase + "/get_topics", {
    method: 'get',
    headers: {"Authorization":"Bearer " + user.access_token},
    retry: false,
    onResponseError({ response }){
        if(response.status === 401){
            useUser.removeUser()
            router.push("/login")
        }else {
            toast.add({
                title: '获取主题列表失败！',
                description: response._data.detail,
                icon: 'i-heroicons-exclamation-triangle',
                color: 'red'
            })
        }
    }
})

async function submitTopic(){
    if(!document.forms.createTopic.checkValidity()){
        return
    }
    await useFetch(useRuntimeConfig().public.apiBase + "/create_topic", {
        method: 'post',
        headers: {"Authorization":"Bearer " + user.access_token},
        retry: false,
        body:  {"title": newTopicTitle.value.trim(), "turn": 1},
        onRequest({ request, options }) {
            isdisabled.value = true
        },
        async onResponse({ request, response, options }) {
            isdisabled.value = false
            if(response.ok === true){
                data.value.todayTopics.splice(0, 0, response._data)
                router.push("/topic/" + response._data.id)
            }else{
                if(response.status === 401){
                    useUser.removeUser()
                    router.push("/login")
                }else {
                    toast.add({
                        title: '创建失败！',
                        description: response._data.detail,
                        icon: 'i-heroicons-exclamation-triangle',
                        color: 'red'
                    })
                }
            }
        }
    })
}

function logout(){
    useUser.removeUser()
    router.push("/login")
}

</script>